mapper.update:
UPDATE transaction
PAYMENT_OPTION = #{paymentOption}, RECEIVER_NAME = #{receiverName} , RECEIVER_PHONE = #{receiverPhone}, DLVY_ADDR = #{divyAddr}, DLVY_REQUEST = #{divyRequest}, DLVY_DATE = #{divyDate}
WHERE tran_no = #{tranNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT tran_no , PROD_NO , BUYER_ID
										FROM transaction
										
											
												
									 				tran_no = #{searchKeyword}
												
												
									 				PROD_NO = #{searchKeyword}
												
												
													BUYER_ID = #{searchKeyword}
												
											
										
										ORDER BY prod_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT tran_no , prod_no , buyer_id
					FROM transaction
					
						
							
					 		tran_no = #{searchKeyword}
							
							
					 		prod_no = #{searchKeyword}
							
							
							BUYER_ID = #{searchKeyword}
							
							
						
					 ) countTable
